<script>
export default {
    data(){
        return {
            sent:false,
            loading:false,
            form:{
                name:'',
                email:'',
                telephone:'',
                message:''
            },
            errors:[],
        }
    },
    methods:{
        send(){
            this.errors = []
            this.loading = true
            axios.post('/contact',this.form).then(res=>{
               this.sent = true
                this.loading = false
            }).catch(err=>{
                this.errors = err.response.data.errors
                this.loading = false
                console.log("data :",this.errors)
            })
        },
        
    }
}
</script>